import React from 'react'

const Header = (props) => {
  return (
    <div>
      <p>
        {props.course}
      </p>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.name} {props.exer}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
        <Part name= {props.p1} exer={props.e1} />
        <Part name= {props.p2} exer={props.e2} />
        <Part name= {props.p3} exer={props.e3} />
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>
        Number of exercises {props.sum} 
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name:'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to passdata',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
}
  return (
    <>
     <h1><Header course= {course} /></h1>
     <Content p1={part1.name} e1={part1.exercises} p2={part2.name} e2={part2.exercises} p3={part3.name} e3={part3.exercises}/>
     <Total sum= {part1.exercises + part2.exercises + part3.exercises} />
    </>
  );
}

export default App;
