import React from 'react'

const Header = (props) => {
  return (
    <div>
      <p>
        {props.course}
      </p>
    </div>
  )
}

const Part = (props) => {
  console.log(props)
  return (
    <div>
      <p>{props.name} {props.exer}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
        <Part name= {props[0].name} exer={props[0].exercises} />
        <Part name= {props[1].name} exer={props[1].exercises} />
        <Part name= {props[2].name} exer={props[2].exercises} />
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>
        Number of exercises {props.sum} 
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
    name:'Fundamentals of React',
    exercises: 10
    },
    {
    name: 'Using props to passdata',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }
  ]
  return (
    <>
     <h1><Header course= {course} /></h1>
     <Content parts={parts} />
     <Total parts= {parts} />
    </>
  );
}

export default App;
