import React from 'react'

const Header = (props) => {
  return (
    <div>
      <p>
        {props.course}
      </p>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.name} {props.exer}</p>
    </div>
  )
}

const Content2 = (props) => {
  return (
    <div>
        <Part name= {props.n1} exer={props.e1} />
        <Part name= {props.n2} exer={props.e2} />
        <Part name= {props.n3} exer={props.e3} />
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>
        Number of exercises {props.sum} 
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to passdata'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <>
     <h1><Header course= {course} /></h1>
     <Content2 n1= {part1} e1={exercises1} n2= {part2} e2={exercises2} n3= {part3} e3={exercises3}/>
     <Total sum= {exercises1 + exercises2 + exercises3} />
    </>
  );
}

export default App;
