import React from 'react'

const Header = (props) => {
  return (
    <div>
      <p>
        {props.course}
      </p>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.name} {props.exer}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
        <Part name= {props.p1} exer={props.e1} />
        <Part name= {props.p2} exer={props.e2} />
        <Part name= {props.p3} exer={props.e3} />
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>
        Number of exercises {props.sum} 
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
    name:'Fundamentals of React',
    exercises: 10
    },
    {
    name: 'Using props to passdata',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }
  ]
  console.log(parts[0].name)
  return (
    <>
     <h1><Header course= {course} /></h1>
     <Content p1={parts[0].name} e1={parts[0].exercises} p2={parts[1].name} e2={parts[1].exercises} p3={parts[2].name} e3={parts[2].exercises}/>
     <Total sum= {parts[0].exercises + parts[1].exercises + parts[3].exercises} />
    </>
  );
}

export default App;
